(window.webpackJsonp=window.webpackJsonp||[]).push([[180],{78:function(t,e,n){"use strict";n.r(e);var s=n(0),r=Object(s.a)({},function(){this.$createElement;this._self._c;return this._m(0)},[function(){var t=this,e=t.$createElement,n=t._self._c||e;return n("div",{staticClass:"content"},[n("h1",{attrs:{id:"财政金融学院简介"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#财政金融学院简介","aria-hidden":"true"}},[t._v("#")]),t._v(" 财政金融学院简介")]),n("blockquote",[n("p",[t._v("本文于2017年录入/高2012级5班彭涧")])]),n("p",[n("strong",[t._v("学院简介：")])]),n("p",[t._v("总的来说，人大财政金融学院是一个非常不错的学院，师资强大，成绩斐然，这方面的具体信息我就不赘述了，网上都有。我重点说一下我的感受。")]),n("p",[t._v("就学习而言，财金院的课程虽然不少，但是课程的难度不算大，尤其像数学、计算机这样的基础学科，考试都不算难，如果你想在金融方面进行深造，建议自行加大难度。但是英语的要求是很高的，我们院长对英语十分重视，要求学生能在“东西方文化间自由漫步”。当然还有各种经济、金融的专业课，其特点是半文半理，既需要一些文科的思维，又需要理科的计算、分析，专业课的考试由各班任课教师自行教授、命题，具体考试内容与难度各班各不相同。")]),n("p",[t._v("说实话，财金院的学习氛围并不是很好，有很多同学的重心并没有放在学习上，但是人大的学习资源是充足的，无论是图书馆、自习室、学术类社团还是老师。只要你自己重视学习，不动摇，完全能够取得巨大收获。")]),n("p",[t._v("就课余活动而言，我们院的活动十分丰富，辩论队、健美操队、各种晚会等等，都是人大做得最出色的。当然，整个人大的课余活动都非常丰富，社团种类很多，办得都很出色，完全能够满足不同爱好学生的需求。")]),n("p",[t._v("欢迎学弟学妹报考人大。")]),n("p",[n("strong",[t._v("学术氛围：")])]),n("p",[t._v("整体来说，这是一个学术氛围不得不浓厚的地方。由于专业课程是学校最多，课程是学校最重的地方，财金的孩子不得不一边兼顾学生会、社团或者社会活动，一边抽时间学习繁重的课程。此外财金还是个学霸和智者云集的地方，学术的氛围不得不在学霸和智者的带领下浓厚起来。")]),n("p",[n("strong",[t._v("活动氛围：")])]),n("p",[t._v("财金作为人民大学的大院之一，一向有大小活动力争第一的传统。除学术外，学院也相当重视文体活动，在新生运动会、校运动会和各种文化节、辩论赛中多次获得桂冠。学院本身还有类似财金之夜等一年一度的文娱晚会，由于是大院，实力雄厚，所以学院自身的活动也常常租用如论讲堂等正规晚会场所。不得不说，财金的活动氛围也是相当浓厚的。")]),n("p",[n("strong",[t._v("学院专业：")])]),n("p",[t._v("财政学、税务、金融学、保险学、金融工程、信用管理")])])}],!1,null,null,null);e.default=r.exports}}]);