(window.webpackJsonp=window.webpackJsonp||[]).push([[329],{302:function(t,e,n){"use strict";n.r(e);var s=n(0),_=Object(s.a)({},function(){this.$createElement;this._self._c;return this._m(0)},[function(){var t=this,e=t.$createElement,n=t._self._c||e;return n("div",{staticClass:"content"},[n("h1",{attrs:{id:"综述"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#综述","aria-hidden":"true"}},[t._v("#")]),t._v(" 综述")]),n("blockquote",[n("p",[t._v("本文于2016年录入/2015级理科15班宋彦霖")])]),n("p",[t._v("学校综述")]),n("p",[t._v("2015年九月，在这金秋时节，我背上行李，踏上这荆楚大地。")]),n("p",[t._v("中南大学坐落在中国历史文化名城──湖南省长沙市，占地面积5886亩，建筑面积276万平方米，跨湘江两岸，依巍巍岳麓，临滔滔湘水，环境幽雅，景色宜人，是求知治学的理想园地。整个学校分为：校本部，南校区，新校区，铁道校区，湘雅校区。一：宿舍：")]),n("p",[t._v("中南大学的大一新生基本都会被分配到南校区住宿。很多学生会惊奇的发现整个南校区基本上只有住宿楼，可以称之为“住宿校区”。这在国内知名大学是比较罕见的。对于寝室而言，不同楼栋的住宿条件不同。但基本上都是4人一间。有些宿舍有独卫，独立阳台；而有些宿舍则是16人大寝室，每个大寝分为四个独立小寝，阳台，洗漱池以及卫生间都为大家公用。所有楼栋均设有热水机器，24小时供应热水。")]),n("p",[t._v("二：食堂：")]),n("p",[t._v("在南校区有三个学生食堂：二食堂，八食堂，以及七食堂。其中数二食堂规模最大，品种最多，被称为“中南餐饮中心”。学校里面的饭菜以辣为主，不管是素菜还是荤菜。总体来说，学校食堂菜品品种齐全，价格公道，各类小吃应有尽有。除此之外，在功课繁忙之际，也可以点外卖小吃。")]),n("p",[t._v("三：校园：")]),n("p",[t._v("中南大学跨湘江两岸，坐落岳麓山脚，校园可谓有得天独厚的地理条件。校园风景优美，各处绿影重重，郁郁葱葱。校园面积较大，建议大一来的新生配备自行车。从南校区到新校区上课步行需要20分钟左右，有自行车则方便很多。")]),n("p",[t._v("四：教学楼，图书馆：")]),n("p",[t._v("中南教学设施齐全，每栋教学楼都配有完备的教学设施，投影仪，空调，电脑等等。并且教学楼大气，美观。每个教室也都宽敞，明亮，必能满足各位学霸学习之心。除此之外，中南大学的新校区图书馆藏书丰富，美观大气，人性化设施齐全，学习气氛浓郁")]),n("p",[t._v("五：生活：")]),n("p",[t._v("生活在中南大学，你会发现无论是购物还是逛街还是想出去聚会，都是十分容易的事情。校园里面遍布超市，水果店。各种生活用品一应俱全，十分方便快捷。如果想要和同学们出去聚餐，唱K，看电影的话，学校附近有商场，购物广场和影城等等。一切都不是事。如果你还想和同学们去商圈里面逛街的话，学校有专门的公交车专线直接载你到长沙市中心商业圈，都是那么的容易。“惟楚有才，于斯为盛”，中南大学你值得拥有。望学弟学妹报名就读。")])])}],!1,null,null,null);e.default=_.exports}}]);